
function calculate_date(b){var b=b.replace(" ","");if(b.indexOf(".")>0){var d=b.split(".")}else{var d=b.split("/")}if(d[2]){var c=d[2]}else{var c=0}var e=d[1];if(e.length==1){e=0+e}var a=d[0];if(a.length==1){a=0+a}return(c+e+a)*1}jQuery.fn.dataTableExt.oSort["eu_date-asc"]=function(d,c){x=calculate_date(d);y=calculate_date(c);return((x<y)?-1:((x>y)?1:0))};jQuery.fn.dataTableExt.oSort["eu_date-desc"]=function(d,c){x=calculate_date(d);y=calculate_date(c);return((x<y)?1:((x>y)?-1:0))};