
function AjaxMaps(a,b){displayLoading();if(b>0){$.ajax({type:"GET",url:a,data:"idMap="+b,success:function(c){hideLoading();$("#boxToolMap").css("display","inline");$("#Map_Apercu").html(c);redraw()}})}else{hideLoading();$("#boxToolMap").css("display","none");$("#countOnMap").html("Aucune carte n'est sélectionnée.");$("#Map_Apercu").html("")}};