
function Update_Monnaie(a){a=$("#inputAmount").val();if($("#inputAccount").val()==""){Barre_De_Statut("Vous n'avez pas indiquer de compte.");Icone_Chargement(2)}else{Barre_De_Statut("Distribution en cours...");Icone_Chargement(1);displayLoading();$.ajax({type:"POST",url:"pages/Admin/ajax/ajaxGererMonnaieExecute.php",data:"nombre_monnaies="+a+"&transaction="+$("#inputTransactionType").val()+"&devise="+$("#selectDevise").val()+"&compte="+$("#inputAccount").val(),success:function(c){hideLoading();var b=JSON.parse(c);if(b.result){popBootbox("Les monnaies ont bien été modifiés");Barre_De_Statut("Transaction effectué.");Icone_Chargement(0)}else{popBootbox(b.reasons);Barre_De_Statut(b.reasons);Icone_Chargement(2)}}})}};