
function Ajax_Appel_Marche(b,a){window.parent.Barre_De_Statut("Appel de l'onglet...");window.parent.Icone_Chargement(1);$.ajax({type:"POST",url:""+b,success:function(c){$("#Contenue_Cadre_Marche").html(c);window.parent.Barre_De_Statut("Chargement terminé.");window.parent.Icone_Chargement(0);if(a!==false){$(".nav-tabs-custom li").attr("class","");$(a).parent("li").attr("class","active")}redraw()}});return false}function Ajax_Appel_Liste(a){window.parent.Barre_De_Statut("Génération de la liste...");window.parent.Icone_Chargement(1);$.ajax({type:"POST",url:"pages/Marche/ajax/ajaxGetArticles.php",data:"race="+$("#Selecteur_Filtre_Ventes_Race").val()+"&sexe="+$("#Selecteur_Filtre_Ventes_Sexe").val()+"&level="+$("#Selecteur_Filtre_Ventes_Level").val()+"&ordre="+$("#Selecteur_Filtre_Ventes_Ordre").val()+"&monnaie="+$("#Selecteur_Filtre_Ventes_Monnaie").val()+"&date="+$("#Selecteur_Filtre_Ventes_Date").val(),success:function(b){$("#Tableau_Liste_Article").html(b);window.parent.Barre_De_Statut("Liste d'articles généré.");window.parent.Icone_Chargement(0);redraw()}});return false}function Ouverture_Dialogue_Achat(a){window.parent.Barre_De_Statut("En attente de la confirmation...");window.parent.Icone_Chargement(1);$("#dialog_Confirmation_Acheter_Article").dialog("open")}function Acquisition_Article(a){bootbox.dialog({message:"Confirmez-vous l'achat de ce personnage ?",animate:false,className:"myBootBox",title:"Confirmation de la demande",buttons:{danger:{label:"Annuler",className:"btn-danger",callback:function(){}},success:{label:"Confirmer",className:"btn-primary",callback:function(){window.parent.Barre_De_Statut("Réalisation de l'achat...");window.parent.Icone_Chargement(1);$.ajax({type:"POST",url:"pages/Marche/ajax/SQL_Procedure_Achat_Personnage.php",data:"id_marche_personnage="+a,success:function(c){try{Parse_Json=JSON.parse(c);if(Parse_Json.result=="WIN"){$.ajax({type:"POST",url:"ajax/Update_Vamonaies.php",success:function(d){window.parent.Fonction_Reteneuse_Vamonaies(d)}});$.ajax({type:"POST",url:"ajax/Update_Tananaies.php",success:function(d){window.parent.Fonction_Reteneuse_Tananaies(d)}});Ajax_Appel_Marche("pages/Marche/MarchePlace.php")}else{if(Parse_Json.result=="FAIL"){window.parent.Barre_De_Statut(Parse_Json.reasons);window.parent.Icone_Chargement(2)}}}catch(b){window.parent.Barre_De_Statut("Annulation échoué.");window.parent.Icone_Chargement(2)}}})}}}})}function Retirer_De_La_Vente(a){bootbox.dialog({message:"Confirmez-vous l'annulation de la vente de ce personnage ?",animate:false,className:"myBootBox",title:"Confirmation de la demande",buttons:{danger:{label:"Annuler",className:"btn-danger",callback:function(){}},success:{label:"Confirmer",className:"btn-primary",callback:function(){window.parent.Barre_De_Statut("Annulation de la vente...");window.parent.Icone_Chargement(1);$.ajax({type:"POST",url:"pages/Marche/ajax/SQL_Retirer_Vente.php",data:"id_marche_personnage="+a,success:function(c){try{Parse_Json=JSON.parse(c);if(Parse_Json.result=="WIN"){Ajax_Appel_Marche("pages/Marche/MarchePlace.php")}else{if(Parse_Json.result=="FAIL"){window.parent.Barre_De_Statut(Parse_Json.reasons);window.parent.Icone_Chargement(2)}}}catch(b){window.parent.Barre_De_Statut("Annulation échoué.");window.parent.Icone_Chargement(2)}}})}}}})};