
function Ajax_Appel_Messagerie(b,a){window.parent.Barre_De_Statut("Appel de l'onglet...");window.parent.Icone_Chargement(1);$.ajax({type:"POST",url:""+b,success:function(c){$("#Contenue_Cadre_Messagerie").html(c);window.parent.Barre_De_Statut("Chargement terminé.");window.parent.Icone_Chargement(0);redraw();if(a!==false){$(".nav-tabs-custom li").attr("class","");$(a).parent("li").attr("class","active")}}});return false}function Ajax_Ouverture_Ticket(a){window.parent.Barre_De_Statut("Ouverture de la discussion...");window.parent.Icone_Chargement(1);$.ajax({type:"POST",url:"pages/Messagerie/MessagerieView.php",data:"id_ticket="+a,success:function(b){$("#Contenue_Cadre_Messagerie").html(b);window.parent.Barre_De_Statut("Chargement terminé.");window.parent.Icone_Chargement(0)}});return false};